StartTest(function(t) {

    t.testExtJS(async t => {
        var panel = new Ext.Panel({
            html    : 'foo',
            title   : 'bar'
        });

        await t.firesOk({
            observable      : panel,
            events          : {
                hide            : 1,
                show            : '<= 1',
                destroy         : '> 0'
            },
            during          : async () => {
                panel.render(Ext.getBody());

                panel.hide();
                panel.show();
                panel.destroy()
            }
        })
    });
});
