var helper      = require('./lib/helper.js')
var chai        = require('chai')

var expect      = chai.expect

describe('Launchers suite', function() {
    
    describe('Browserstack sanity', function () {
        
        this.timeout(90000)
        
        it('Should be possible to get the coverage report', function (done) {
            
            helper.runCoverageSuite({ 
                browserstack    : process.env.BS_USER + ',' + process.env.BS_KEY
            }, function (res) {
                done()
            })
        });
        
        
        it('Should be possible to get the JSON report', function (done) {
            
            helper.runJSONReportSuite({ 
                browserstack    : process.env.BS_USER + ',' + process.env.BS_KEY
            }, function (res) {
                done()
            })
        });
    });
});