var helper      = require('./lib/helper.js')
var chai        = require('chai')

var expect      = chai.expect

describe('Launchers suite', function() {

    describe('SauceLabs sanity', function () {

        this.timeout(90000)

        it('Should be possible to get the coverage report', function (done) {

            helper.runCoverageSuite({ $noXvfb : true, saucelabs : process.env.SL_USER + ',' + process.env.SL_KEY + ',lh,local' }, function (res) {
                done()
            })
        });


        it('Should be possible to get the JSON report', function (done) {

            helper.runJSONReportSuite({ $noXvfb : true, saucelabs : process.env.SL_USER + ',' + process.env.SL_KEY + ',lh,local' }, function (res) {
                done()
            })
        });
    });
});
