StartTest(function(t) {

    var siestaRoot = window.top.location.href.substring(0, window.top.location.href.toLowerCase().indexOf('siesta') + 7);

    t.chain(
        { setUrl : siestaRoot + '/examples/browser' },

        { click : 'treefilter => .fa-close' },

        { waitForEvent : function () { return [ t.global.project, 'testsuiteend' ] }, trigger : { dblclick : '.x-grid-item:contains(basic_assertions)' } },

        { screenshot : 'build/doc-images/images/welcome.png'}
    )
})