Role('Siesta.Util.Role.CanDetectES6', {

    has : {
        supportsArrowFunctions : function () {
            return this.canEvaluate('x => x')
        }
    },

    methods : {
        canEvaluate : function (str) {
            try {
                eval(str)
                return true
            } catch (e) {
                return false
            }
        }
    }
})
