import {Constructable, Mixin} from "../../../generic/util/Common.js";
import {CanLog} from "../../../generic/util/role/CanLog.js";


export const BrowserLogger = <T extends Constructable>(base : T) => class BrowserLogger extends CanLog<Constructable>(base) {

    printLn (message : string) {
        console.log(message)
    }
}

export type BrowserLogger = Mixin<typeof BrowserLogger>
